INSERT INTO `lol_items` (`id`, `name`, `plaintext`, `description`, `riot_id`) VALUES
(264, 'Warden\'s Mail', 'Slows Attack Speed of enemy champions when receiving basic attacks', '<stats>+40 Armor</stats><br><br><unique>UNIQUE Passive - Cold Steel:</unique> When hit by basic attacks, reduces the attacker\'s Attack Speed by 15% for 1 seconds.', 3082),
(265, 'Flash Zone', 'Allows you and allies to repeatedly flash while in a zone', '<br><font color=\'#FF9900\'>Allows team to cast Flash repeatedly in a limited zone.</font><br><br>Creates a magic zone for your team for 5 seconds. While in this zone, you and your allies have your summoner spells replaced by an instant cast blink that moves you to any location in the zone (1 second cooldown).', 3640),
(266, 'Rabadon\'s Deathcap', 'Massively increases Ability Power', '<stats>+120 Ability Power </stats><br><br><unique>UNIQUE Passive:</unique> Increases Ability Power by 35%.', 3089),
(267, 'Statikk Shiv', 'Movement builds charges that release chain lightning on basic attack', '<stats>+35% Attack Speed<br>+30% Critical Strike Chance<br>+5% Movement Speed</stats><br><br><passive>Passive:</passive> Moving and attacking will make an attack <a href=\'Energized\'>Energized</a>.<br><br><unique>UNIQUE Passive - Shiv Lightning:</unique> Your Energized attacks deal 60~160 bonus magic damage (based on level) to up to 5 targets on hit (deals +65% bonus damage to minions and can critically strike).', 3087),
(268, 'Zeal', 'Slight bonuses to Critical Strike Chance, Movement Speed and Attack Speed', '<stats>+15% Attack Speed<br>+20% Critical Strike Chance</stats><br><br><unique>UNIQUE Passive:</unique> +5% Movement Speed', 3086),
(269, 'Runaan\'s Hurricane', 'Ranged attacks fire two bolts at nearby enemies', '<stats>+40% Attack Speed<br>+30% Critical Strike Chance<br>+7% Movement Speed</stats><br><br><unique>UNIQUE Passive - Wind\'s Fury:</unique> When basic attacking, bolts are fired at up to 2 enemies near the target, each dealing (40% of Attack Damage) physical damage. Bolts can critically strike and apply on hit effects.', 3085),
(270, 'Overlord\'s Bloodmail', 'Restores Health on kill or assist', '<stats>+800 Health<br>+100% Base Health Regen </stats><br><br><unique>UNIQUE Passive:</unique> Upon champion kill or assist, restores 300 Health over 5 seconds.', 3084),
(271, 'Warmog\'s Armor', 'Grants massive Health and Health Regen', '<stats>+800 Health<br>+200% Base Health Regen </stats><br><br><unique>UNIQUE Passive:</unique> +10% Cooldown Reduction<br><unique>UNIQUE Passive:</unique> Grants <unlockedPassive>Warmog\'s Heart</unlockedPassive> if you have at least 2750 maximum Health.<br><br><unlockedPassive>Warmog\'s Heart:</unlockedPassive> Restores 25% of maximum Health every 5 seconds if damage hasn\'t been taken within 6 seconds (3 seconds for damage from minions and monsters).', 3083),
(272, 'Eye of the Equinox', 'Provides Health and Stealth Wards over time', '<stats>+500 Health<br>+200% Base Health Regen <br>+10% Cooldown Reduction<br>+2 Gold per 10 seconds</stats><br><br><unique>UNIQUE Passive - Spoils of War:</unique> Melee basic attacks execute minions below 320 (+20 per level) Health. Killing a minion heals the owner and the nearest allied champion for 50 Health and grants them kill Gold. These effects require a nearby ally. Recharges every 30 seconds. Max 4 charges.<br><active>UNIQUE Active - Warding:</active> Consumes a charge to place a <font color=\'#BBFFFF\'>Stealth Ward</font> that reveals the surrounding area for 150 seconds. Holds up to 4 charges which refill upon visiting the shop.<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Shield Battery</font>, a permanent shield that regenerates slowly outside of combat.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 2303),
(273, 'Eye of the Herald', 'Eye of the Herald - a Gift of the Void.', '<br><unique>UNIQUE Passive - Glimpse of the Void:</unique> The holder of the Eye of the Herald has Empowered Recall.<br><br><active>UNIQUE Active:</active> Channel for 3.5 seconds to crush the Eye of the Herald, summoning the Rift Herald to siege enemy turrets.<br><br>The Eye of the Herald will be lost to the Void if not used within 240 seconds.</font>', 3513),
(274, 'Eye of the Oasis', 'Provides Gold, Mana, and Stealth Wards over time', '<stats>+200 Health<br>+125% Base Health Regen <br>+10% Cooldown Reduction<br>+2 Gold per 10 seconds</stats><br><br><unique>UNIQUE Passive - Favor: </unique>Enemy minions killed by your allies sometimes drop coins that give either <font color=\'#D4AF37\'>40</font> gold or <font color=\'#44DDFF\'>6%</font> missing mana (minimum 10). Cannon minions always drop coins.<br><active>UNIQUE Active - Warding:</active> Consumes a charge to place a <font color=\'#BBFFFF\'>Stealth Ward</font> that reveals the surrounding area for 150 seconds. Holds up to 4 charges which refill upon visiting the shop<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Favor</font> is upgraded to <font color=\'#CFBF84\'><a href=\'coinlinequestreward\'>Emperor\'s Favor</a></font> and allied champions moving toward you gain 8% movement speed.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 2302),
(275, 'Zz\'Rot Portal', 'Makes a Voidspawn generating Void Gate to push a lane with.', '<stats>+55 Armor<br>+55 Magic Resist<br>+125% Base Health Regen <br></stats><br><unique>UNIQUE Passive - Point Runner:</unique> Builds up to +20% Movement Speed over 2 seconds while near turrets, fallen turrets and Void Gates.<br><active>UNIQUE Active:</active> Spawns a <a href=\'VoidGate\'>Void Gate</a> for 120 seconds (120 second cooldown).<br><br>Every 4 seconds the gate makes a <a href=\'Voidspawn\'>Voidspawn</a>. The first and every fourth Voidspawn gains 15% of maximum Health as damage.', 3512),
(276, 'Eye of the Watchers', 'Provides Ability Power and Stealth Wards over time', '<stats>+200 Health<br><mana>+50% Base Mana Regen </mana><br>+35 Ability Power<br>+10% Cooldown Reduction<br>+2 Gold per 10 seconds</stats><br><br><unique>UNIQUE Passive - Tribute:</unique> Damaging spells and attacks against champions or buildings deal 15 additional magic damage and grant 15 Gold. This can occur up to 3 times every 30 seconds.<br><active>UNIQUE Active - Warding:</active> Consumes a charge to place a <font color=\'#BBFFFF\'>Stealth Ward</font> that reveals the surrounding area for 150 seconds. Holds up to 4 charges which refill upon visiting the shop.<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Tribute</font> is upgraded into <font color=\'#CFBF84\'><a href=\'frostqueenslinequestreward\'>Queen\'s Tribute</a></font>.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 2301),
(277, 'Siege Teleport', '', '<unique>Active:</unique> Use this trinket to teleport to one of your team\'s port pads. Can only be used from the summoning platform.', 3630),
(278, 'Bami\'s Cinder', 'Grants Health and Immolate Aura', '<stats>+200 Health </stats><br><br><unique>UNIQUE Passive - Immolate:</unique> Deals 5 (+1 per champion level) magic damage per second to nearby enemies. Deals 100% bonus damage to minions and monsters.', 3751),
(279, 'Siege Ballista', 'Place a long range anti-turret ballista', '<br><font color=\'#FF9900\'>Deploys a ballista that shoots the closest turret.</font><br><br>Places a long range ballista if within 2200 range of an enemy turret. After a 5 second delay, it will begin firing at the nearest enemy turret, dealing heavy damage. If the targeted turret expires, the ballista will as well.', 3631),
(280, '', '', '', 3632),
(281, 'Siege Teleport', '', '<unique>Active:</unique> Use this trinket to teleport to one of your team\'s port pads. Can only be used from the summoning platform.', 3633),
(282, 'Tower: Beam of Ruination', 'Attaches a three shot beam to a turret which can then be aimed and fired', '<br><font color=\'#FF9900\'>Attach, then recast to fire a damaging beam from a turret to your cursor.</font><br><br><font color=\'#FF9900\'>First Cast:</font> Attach a Slayer Beam to the target turret that can be fired 3 times.<br></br><font color=\'#FF9900\'>Next Three Casts:</font> Fires the attached beam towards your cursor, dealing 30/level + 30% of the hit target\'s maximum health (20% damage to minions) in magic damage to all targets in a line.<br></br><br></br>Beam will last 15 seconds, or until it has been fired 3 times.', 3634),
(283, 'Port Pad', 'Creates another point for your team to Teleport to', '<br><font color=\'#FF9900\'>Deploy an additional teleport target.</font><br><br>Places a Port Pad at target location. After a 4 second delay, it activates, allowing you or your allies to teleport to it from base.', 3635),
(284, 'Tower: Storm Bulwark', 'Make a turret go invulnerable while charging a powerful barrage', '<br><font color=\'#FF9900\'>Makes a turret go invulnerable, then rain fire.</font><br><br>Makes the target turret invulnerable for 6 seconds. Two seconds before expiry, it unleashes a missile volley, dealing 2600 true damage over the remaining time to all nearby enemies.<br><br>Cannot be used on the same turret more than once in 15 seconds.', 3636),
(285, 'Nexus Siege: Siege Weapon Slot', '', 'In Nexus Siege, Summoner Spells are replaced with Siege Weapon Slots. Spend Crystal Shards to buy single-use Siege Weapons from the item shop, then use your Summoner Spell keys to activate them!', 3637),
(286, 'Frost Queen\'s Claim', 'Sends out seeking wraiths that track hidden enemies and slow them', '<stats>+60 Ability Power<br>+10% Cooldown Reduction<br>+2 Gold per 10 seconds<br><mana>+50% Base Mana Regen </mana></stats><br><br><unique>UNIQUE Passive - Tribute:</unique> Damaging spells and attacks against champions or buildings deal 15 additional magic damage and grant 15 Gold. This can occur up to 3 times every 30 seconds.<br><active>UNIQUE Active:</active> Summon 2 icy ghosts for 6 seconds that seek out nearby enemy champions. Ghosts reveal enemies on contact and slow them by 40% for between 2 and 5 seconds based on how far the ghosts have traveled (90 second cooldown).<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Tribute</font> is upgraded into <font color=\'#CFBF84\'><a href=\'frostqueenslinequestreward\'>Queen\'s Tribute</a></font>.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 3092),
(287, 'Wit\'s End', 'Deals bonus magic damage on basic attacks', '<stats>+40% Attack Speed<br>+40 Magic Resist</stats><br><br><unique>UNIQUE Passive:</unique> Basic attacks deal 40 bonus magic damage on hit.<br><unique>UNIQUE Passive:</unique> Basic attacks steal 5 Magic Resist from the target on hit (stacks up to 5 times.)', 3091),
(288, 'Wooglet\'s Witchcap', 'Massively increases Ability Power and can be activated to enter stasis', '<stats>+100 Ability Power<br>+45 Armor </stats><br><br><unique>UNIQUE Passive:</unique> Increases Ability Power by 25%<br><active>UNIQUE Active:</active> Champion becomes invulnerable and untargetable for 2.5 seconds, but is unable to move, attack, cast spells, or use items during this time (120 second cooldown).', 3090),
(289, 'Frostfang', 'Grants gold when you damage an enemy', '<stats>+20 Ability Power<br>+2 Gold per 10 seconds<br><mana>+50% Base Mana Regen </mana><br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive - Tribute:</unique> Damaging spells and attacks against champions or buildings deal 15 additional magic damage and grant 15 Gold. This can occur up to 3 times every 30 seconds. Killing minions slows Tribute generation.<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Tribute</font> is upgraded into <font color=\'#CFBF84\'><a href=\'frostqueenslinequestreward\'>Queen\'s Tribute</a></font>.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 3098),
(290, 'Targon\'s Brace', 'Periodically kill enemy minions to heal and grant gold to a nearby ally', '<stats>+175 Health<br>+50% Base Health Regen <br>+2 Gold per 10 seconds </stats><br><br><unique>UNIQUE Passive - Spoils of War:</unique> Melee basic attacks execute minions below 200 (+10 per level) Health. Killing a minion heals the owner and the nearest allied champion for 40 Health and grants them kill Gold.<br><br>These effects require a nearby ally. Recharges every 30 seconds. Max 3 charges.<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Shield Battery</font>, a permanent shield that regenerates slowly outside of combat.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 3097),
(291, 'Nomad\'s Medallion', 'Grants gold and mana when nearby minions die that you didn\'t kill', '<stats>+50% Base Health Regen <br>+10% Cooldown Reduction<br>+2 Gold per 10 seconds</stats><br><br><unique>UNIQUE Passive - Favor:</unique> Enemy minions killed by your allies sometimes drop coins that give either <font color=\'#D4AF37\'>40</font> gold or <font color=\'#44DDFF\'>6%</font> missing mana (minimum 10). Cannon minions always drop coins.<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Favor</font> is upgraded to <font color=\'#CFBF84\'><a href=\'coinlinequestreward\'>Emperor\'s Favor</a></font> and allied champions moving toward you gain 8% movement speed.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit><br><br><rules><font color=\'#447777\'>\'\'The medallion shines with the glory of a thousand voices when exposed to the sun.\'\' - Historian Shurelya, 22 June, 24 CLE</font></rules>', 3096),
(292, 'Rapid Firecannon', 'Movement builds charges that release a sieging fire attack on release', '<stats>+30% Attack Speed<br>+30% Critical Strike Chance<br>+5% Movement Speed</stats><br><br><passive>Passive:</passive> Moving and attacking will make an attack <a href=\'Energized\'>Energized</a>.<br><br><unique>UNIQUE Passive - Firecannon:</unique> Your Energized attacks gain 35% bonus Range (+150 range maximum) and deal 50~120 bonus magic damage (based on level) on hit.<br><br>Attacks become Energized 25% faster. Energized attacks function on structures.', 3094),
(293, 'Siege Sight Warder', '', '<groupLimit>Limited to 1 Trinket.</groupLimit><br><br><active>Active:</active> Places a <font color=\'#FFFFFF\'>Stealth Ward</font> that lasts <font color=\'#FFFFFF\'>30</font> seconds (30 second cooldown).', 3649),
(294, 'Face of the Mountain', 'Shield an ally from damage based on your Health', '<stats>+450 Health<br>+100% Base Health Regen <br>+10% Cooldown Reduction<br>+2 Gold per 10 seconds </stats><br><br><unique>UNIQUE Passive - Spoils of War:</unique> Melee basic attacks execute minions below 320 (+20 per level) Health. Killing a minion heals the owner and the nearest allied champion for 50 Health and grants them kill Gold. These effects require a nearby ally. Recharges every 30 seconds. Max 4 charges.<br><unique>UNIQUE Active:</unique> Grant a shield to you and an ally equal to 10% of your maximum Health for 4 seconds. After 4 seconds, the shields explode to slow nearby enemies by 40% for 2 seconds (60 second cooldown). Automatically targets the most wounded ally if cast upon self.<hr><passive>QUEST:</passive> Earn 650 gold using this item.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Shield Battery</font>, a permanent shield that regenerates slowly outside of combat.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 3401),
(295, 'Vanguard Banner', 'Strengthens nearby minions', '<br><font color=\'#FF9900\'>Place a banner that buffs minions.</font><br><br>Place a Vanguard Banner at target location. After a 2 second delay, any nearby minions will be granted a buff, increasing their damage by 50%, and granting them 50 Armor and 100 Magic Resistance while within range.', 3641),
(296, 'Siege Refund', 'Refunds all current Siege Weapons', 'Refunds all purchased Siege Weapons for their full price.', 3642),
(297, 'Entropy Field', 'Places a field that stuns enemy minions and slows champions', '<br><font color=\'#FF9900\'>Stun minions and slow champions in an area.</font><br><br>Places an Entropy Field at target location for 5 seconds. Enemy minions and Siege Ballistas trapped in the field are unable to move or attack while in the field. Enemy champions in the field have their Movement Speed reduced by 25%.', 3643),
(298, 'Seer Stone (Trinket)', 'Briefly reveals a nearby targeted area', '<groupLimit>Limited to 1 Trinket.</groupLimit><br><br><active>Active:</active> Reveals a small area within <font color=\'#FFFFF\'>1400</font> range for 3 seconds. Enemy champions will be revealed for 5 seconds (60 second cooldown)', 3645),
(299, 'Shield Totem', 'Grants bonus health to nearby Siege Weapons', '<br><font color=\'#FF9900\'>Place a totem that shields nearby deployables.</font><br><br>Places a Shield Totem at target location. After a 2 second delay, the totem will activate, granting a 2 (+1 per additional Shield Totem) strength shield to all nearby deployables.', 3647),
(300, 'Siege Teleport (Inactive)', '', '', 3648),
(301, 'Head of Kha\'Zix', '', '<unique>UNIQUE Active - Scrying:</unique> Reveals a small location within 4000 range for 2 seconds. Enemy champions found will be revealed for 5 seconds (90 second cooldown).<br><br><unique>UNIQUE Passive - Mementos of the Hunt:</unique> Rengar collects trophies when killing Champions and gains bonus effects based on how many trophies he has. Kills and assists grant 1 trophy.<br><br><passive>3 Trophies:</passive> Rengar gains 25 Movement Speed whilst out of combat or in brush. <br><passive>6 Trophies:</passive> Increases the range of Rengar\'s Leap by 125.<br><passive>12 Trophies:</passive> Thrill of the Hunt\'s duration is increased by 5 seconds.<br><passive>20 Trophies:</passive> Thrill of the Hunt\'s Movement Speed while stealthed is doubled.', 3416),
(302, 'Head of Kha\'Zix', '', '<unique>UNIQUE Active - Sweeping Lens:</unique> Reveals and disables nearby invisible traps and invisible wards for 6 seconds in a medium radius and grants detection of invisible units for 10 seconds (60 second cooldown).<br><br><unique>UNIQUE Passive - Mementos of the Hunt:</unique> Rengar collects trophies when killing Champions and gains bonus effects based on how many trophies he has. Kills and assists grant 1 trophy.<br><br><passive>3 Trophies:</passive> Rengar gains 25 Movement Speed whilst out of combat or in brush. <br><passive>6 Trophies:</passive> Increases the range of Rengar\'s Leap by 125.<br><passive>12 Trophies:</passive> Thrill of the Hunt\'s duration is increased by 5 seconds.<br><passive>20 Trophies:</passive> Thrill of the Hunt\'s Movement Speed while stealthed is doubled.', 3410),
(303, 'Enchantment: Warrior', '', '<stats>+60 Attack Damage<br>+10% Cooldown Reduction</stats>', 3671),
(304, 'Enchantment: Cinderhulk', '', '<stats>+325 Health<br>+15% Bonus Health</stats><br><br><unique>UNIQUE Passive - Immolate:</unique> Deals 7 (+2 per champion level) magic damage a second to nearby enemies while in combat. Deals 100% bonus damage to monsters. ', 3672),
(305, 'Enchantment: Runic Echoes', '', '<stats>+60 Ability Power<br>+7% Movement Speed</stats><br><br><unique>UNIQUE Passive - Echo:</unique> Gain charges upon moving or casting. At 100 charges, the next damaging spell hit expends all charges to deal 60 (+10% of Ability Power) bonus magic damage to up to 4 targets on hit.<br><br>This effect deals 250% damage to Large Monsters. Hitting a Large Monster with this effect will restore 18% of your missing Mana.', 3673),
(306, 'Giant\'s Belt', 'Greatly increases Health', '<stats>+380 Health</stats>', 1011),
(307, 'Rejuvenation Bead', 'Slightly increases Health Regen', '<stats>+50% Base Health Regen </stats>', 1006),
(308, 'Faerie Charm', 'Slightly increases Mana Regen', '<stats><mana>+25% Base Mana Regen </mana></stats>', 1004),
(309, 'Spellthief\'s Edge', 'Grants gold when you damage enemies', '<stats>+10 Ability Power<br>+2 Gold per 10 seconds<br><mana>+25% Base Mana Regen </mana></stats><br><br><unique>UNIQUE Passive - Tribute:</unique> Damaging spells and attacks against champions or buildings deal 10 additional magic damage and grant 8 Gold. This can occur up to 3 times every 30 seconds. Killing minions slows Tribute generation.<hr><passive>QUEST:</passive> Earn 650 gold using this item and upgrade to <font color=\'#CFBF84\'>Frostfang</font>.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Tribute</font> is upgraded into <font color=\'#CFBF84\'><a href=\'frostqueenslinequestreward\'>Queen\'s Tribute</a></font>.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 3303),
(310, 'Relic Shield', 'Kill minions periodically to heal and grant gold to a nearby ally', '<stats>+75 Health<br>+2 Gold per 10 seconds </stats><br><br><unique>UNIQUE Passive - Spoils of War:</unique> Melee basic attacks execute minions below 195 (+5 per level) Health. Killing a minion heals the owner and the nearest allied champion for 15 Health and grants them kill Gold. These effects require a nearby ally. Recharges every 40 seconds. Max 2 charges.<hr><passive>QUEST:</passive> Earn 650 gold using this item and upgrade to <font color=\'#CFBF84\'>Targon\'s Brace</font>.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Shield Battery</font>, a permanent shield that regenerates slowly outside of combat.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit>', 3302),
(311, 'Ancient Coin', 'Grants gold and mana when nearby minions die that you didn\'t kill', '<stats>+5% Cooldown Reduction<br>+2 Gold per 10 seconds</stats><br><br><unique>UNIQUE Passive - Favor:</unique> Enemy minions killed by your allies sometimes drop coins that give either <font color=\'#D4AF37\'>25</font> gold or <font color=\'#44DDFF\'>6%</font> missing mana (minimum 10). Cannon minions always drop coins.<hr><passive>QUEST:</passive> Earn 650 gold using this item and upgrade to <font color=\'#CFBF84\'>Nomad\'s Medallion</font>.<br><passive>REWARD:</passive> <font color=\'#CFBF84\'>Favor</font> is upgraded to <font color=\'#CFBF84\'><a href=\'coinlinequestreward\'>Emperor\'s Favor</a></font> and allied champions moving toward you gain 8% movement speed.<br><br><groupLimit>Limited to 1 Gold Income Item.</groupLimit><br><br><i><font color=\'#447777\'>\'\'Gold dust rises from the desert and clings to the coin.\'\' - Historian Shurelya, 11 November, 23 CLE</font></i>', 3301),
(312, 'Head of Kha\'Zix', '', '<unique>UNIQUE Passive - Mementos of the Hunt:</unique> Rengar collects trophies when killing Champions and gains bonus effects based on how many trophies he has. Kills and assists grant 1 trophy.<br><br><passive>3 Trophies:</passive> Rengar gains 25 Movement Speed whilst out of combat or in brush. <br><passive>6 Trophies:</passive> Increases the range of Rengar\'s Leap by 125.<br><passive>12 Trophies:</passive> Thrill of the Hunt\'s duration is increased by 5 seconds.<br><passive>20 Trophies:</passive> Thrill of the Hunt\'s Movement Speed while stealthed is doubled.', 3422),
(313, 'Boots of Speed', 'Slightly increases Movement Speed', '<groupLimit>Limited to 1.</groupLimit><br><br><unique>UNIQUE Passive - Enhanced Movement:</unique> +25 Movement Speed', 1001),
(314, 'Frosted Snax', 'King: Fires a barrage of icy artillery', '<active>Active - <a href=\'FeedTheKing\'>Feed The King</a>:</active> The King lobs many projectiles at far-away enemies, each dealing <scaleLevel>213-775</scaleLevel> magic damage to targets in the center of the impact, scaling down to <scaleLevel>85-310</scaleLevel> on the edge. (120s cooldown)', 3680),
(315, 'Super Spicy Snax', 'King: Shoots flames that burn units and Turrets', '<active>Active - <a href=\'FeedTheKing\'>Feed The King</a>:</active> The King breathes fire for 4 seconds, dealing <scaleLevel>705-1479</scaleLevel> true damage over the duration to enemies caught in the cone. Deals up to 560 true damage to Turrets. (120s cooldown)', 3681),
(316, 'Espresso Snax', 'King: Knocks back and grants a large shield', '<active>Active - <a href=\'FeedTheKing\'>Feed The King</a>:</active> The King leaps into the air and crashes down twice, knocking enemies away and dealing <scaleLevel>40-190</scaleLevel> physical damage. He also gains a decaying shield for <font color=\'#FF3300\'>20% of his maximum health</font>, lasting 4 seconds. (30s cooldown)', 3682),
(317, 'Rainbow Snax Party Pack!', 'King: Poros knock enemies towards him', '<active>Active - <a href=\'FeedTheKing\'>Feed The King</a>:</active> The King tosses many Snax behind the enemy, attracting Poros which dash back towards him. Enemy champions hit will be knocked forwards and dealt <scaleLevel>230-680</scaleLevel> physical damage. (120s cooldown)', 3683),
(318, 'Prototype Hex Core', 'Increases Ability Power and can be upgraded to improve Viktor\'s abilities', '<stats>+1 Ability Power per level<br>+10 Mana per level</stats><br><br><unique>UNIQUE Passive - Progress:</unique> This item can be upgraded three times to enhance Viktor\'s basic abilities.', 3200),
(319, 'Cloak of Agility', 'Increases critical strike chance', '<stats>+20% Critical Strike Chance</stats>', 1018),
(320, 'Enchantment: Bloodrazor', '', '<stats>+50% Attack Speed</stats><br><br><unique>UNIQUE Passive:</unique> Basic attacks deal 4% of the target\'s maximum Health in bonus physical damage (max 75 vs. monsters and minions) on hit.', 3675),
(321, 'Cosmic Shackle', '', '<passive>Passive - Cosmic Shackle: </passive>Death Sentence pulls much farther (based on the target\'s Missing Health), and can be ignited by the Dark Star to do more damage.<br><br><flavorText>\'\'A still more glorious dawn awaits.\'\'</flavorText>', 3690),
(322, 'Singularity Lantern', '', '<passive>Passive - Singularity Lantern: </passive>Dark Passage automatically saves disabled allies. However, it no longer provides a shield.<br><br><flavorText>\'\'The stars call to us.\'\'</flavorText>', 3691),
(323, 'Dark Matter Scythe', '', '<passive>Passive - Dark Matter Scythe: </passive>Flay\'s on-hit passive charges damage very quickly. Flay will throw enemies much farther (based on their Missing Health).<br><br><flavorText>\'\'If you want to make a Singularity from scratch, you must first destroy the universe.\'\'</flavorText>', 3692),
(324, 'Gravity Boots', '', '<passive>Passive - Mass Conversion: </passive>Thresh\'s Health represents how far enemy pulls and pushes will send him. At lower Health, he will be thrown farther.<br><br><passive>Passive - Terminus Dwellers: </passive>Abyss Scuttlers emerge periodically, and will scurry towards the Dark Star when attacked. Gravitational disturbances will temporarily attract many of them.', 3693),
(325, 'Cloak of Stars', '', '<passive>Passive - Stellar Spirit: </passive>Upon spawning, Thresh is invulnerable, untargetable, cannot cast, and is able to travel in open space. This is lost when stepping foot on stable ground.<br><br>Being saved by Dark Passage or using Death Sentence on one of the three <font color=\'#3091ec\'>Gravity Anchors</font> will briefly put you into this invulnerable state and break enemy chains on you.', 3694),
(326, 'Dark Star Sigil', '', '<passive>Passive - Stellar Fealty: </passive>Thresh cannot kill units directly - their souls, experience, and gold belong to the Dark Star.<br><br>Pulling or pushing an enemy into the Dark Star will destroy them instantly, scoring points for your team (+5, or +1 for Abyss Scuttlers).<br><br>Winning a round requires 100 points, and the final points must be from a champion kill.', 3695),
(327, 'Null-Magic Mantle', 'Slightly increases Magic Resist', '<stats>+25 Magic Resist</stats>', 1033),
(328, 'Spectre\'s Cowl', 'Improves defense and grants regeneration upon being damaged', '<stats>+250 Health<br>+25 Magic Resist</stats><br><br><unique>UNIQUE Passive:</unique> Grants 150% Base Health Regen for up to 10 seconds after taking damage from an enemy champion.', 3211),
(329, 'Chain Vest', 'Greatly increases Armor', '<stats>+40 Armor</stats>', 1031),
(330, 'Cloth Armor', 'Slightly increases Armor', '<stats>+15 Armor</stats>', 1029),
(331, 'Ruby Crystal', 'Increases Health', '<stats>+150 Health</stats>', 1028),
(332, 'Sapphire Crystal', 'Increases Mana', '<stats><mana>+250 Mana</mana></stats>', 1027),
(333, 'Blasting Wand', 'Moderately increases Ability Power', '<stats>+40 Ability Power</stats>', 1026),
(334, 'Stinger', 'Increased Attack Speed and Cooldown Reduction', '<stats>+35% Attack Speed</stats><br><br><unique>UNIQUE Passive:</unique> +10% Cooldown Reduction', 3101),
(335, 'Mikael\'s Crucible', 'Activate to remove all disabling effects from an allied champion', '<stats>+40 Magic Resist<br>+10% Cooldown Reduction<br><mana>+100% Base Mana Regen </mana></stats><br><br><unique>UNIQUE Passive:</unique> +20% Heal and Shield Power<br><unique>UNIQUE Passive - Harmony:</unique> Grants bonus % Base Health Regen equal to your bonus % Base Mana Regen.<br><active>UNIQUE Active:</active> Cleanses all stuns, roots, taunts, fears, silences, and slows on an allied champion and grants them slow immunity for 2 seconds (120 second cooldown). <br><br>Cleansing an effect grants the ally 40% movement speed for 2 seconds.', 3222),
(336, 'Recurve Bow', 'Greatly increases Attack Speed', '<stats>+25% Attack Speed</stats><br><br><unique>UNIQUE Passive:</unique> Basic attacks deal an additional 15 physical damage on hit.', 1043),
(337, 'Elixir Of Skill', '', '<consumable>Click to Consume:</consumable> Grants <font color=\'#29E3D6\'>+1 Skill Point</font>.', 2011),
(338, 'Lich Bane', 'Grants a bonus to next attack after spell cast', '<stats>+80 Ability Power<br>+7% Movement Speed<br>+10% Cooldown Reduction<br><mana>+250 Mana</mana></stats><br><br><unique>UNIQUE Passive - Spellblade:</unique> After using an ability, the next basic attack deals 75% Base Attack Damage (+50% of Ability Power) bonus magic damage on hit (1.5 second cooldown).', 3100),
(339, 'Dagger', 'Slightly increases Attack Speed', '<stats>+12% Attack Speed</stats>', 1042),
(340, 'Total Biscuit of Rejuvenation', '', '<consumable>Click to Consume:</consumable> Restores 15 Health and 15 Mana immediately and then 150 Health over 15 seconds.', 2010),
(341, 'Sweeping Lens (Trinket)', 'Detects and disables nearby invisible wards and traps', '<groupLimit>Limited to 1 Trinket.</groupLimit><br><br><active>Active:</active> Scans an area for 6 seconds, warning against hidden hostile units and revealing invisible traps and revealing / disabling wards (90 to 60 second cooldown).<br><br>Cast range and sweep radius gradually improve with level.<br><br><rules>(Switching to a <font color=\'#BBFFFF\'>Totem</font>-type trinket will disable <font color=\'#BBFFFF\'>Trinket</font> use for 120 seconds.)</rules>', 3341),
(342, 'Seer Stone (Trinket)', 'Briefly reveals a nearby targeted area', '<groupLimit>Limited to 1 Trinket.</groupLimit><br><br><active>Active:</active> Reveals a small area within <font color=\'#FFFFF\'>2500</font> range for 3 seconds. Enemy champions will be revealed for 5 seconds (60 second cooldown)', 3462),
(343, 'Hunter\'s Machete', 'Provides damage and life steal versus Monsters', '<stats>+10% Life Steal vs. Monsters</stats><br><br><unique>UNIQUE Passive - Nail:</unique> Basic attacks deal 25 bonus damage on hit vs. Monsters. Killing monsters grants <font color=\'#99BBBB\'><a href=\'SpecialJungleExperience\'>special bonus experience</a></font>.', 1041),
(344, 'Warding Totem (Trinket)', 'Periodically place a Stealth Ward', '<groupLimit>Limited to 1 Trinket.</groupLimit><br><br><active>Active:</active> Consume a charge to place an invisible <font color=\'#BBFFFF\'>Stealth Ward</font> which reveals the surrounding area for <scaleLevel>60 - 120</scaleLevel> seconds. <br><br>Stores one charge every <scaleLevel>180 - 90</scaleLevel> seconds, up to 2 maximum charges.<br><br>Ward duration and recharge time gradually improve with level.<br><br><rules>(Limit 3 <font color=\'#BBFFFF\'>Stealth Wards</font> on the map per player. Switching to a <font color=\'#BBFFFF\'>Lens</font> type trinket will disable <font color=\'#BBFFFF\'>Trinket</font> use for 120 seconds.)</rules>', 3340),
(345, 'Golden Transcendence (Disabled)', '', '<unique>Active:</unique> Use this trinket to teleport to one of the battle platforms. Can only be used from the summoning platform.<br><br><i><font color=\'#FDD017\'>\'\'It is at this magical precipice where a champion is dismantled, reforged, and empowered.\'\'</font></i>', 3461),
(346, 'Golden Transcendence', '', '<unique>Active:</unique> Use this trinket to teleport to one of the battle platforms. Can only be used from the summoning platform.<br><br><i><font color=\'#FDD017\'>\'\'It is at this magical precipice where a champion is dismantled, reforged, and empowered.\'\'</font></i>', 3460),
(347, 'Total Biscuit of Rejuvenation', '', '<consumable>Click to Consume:</consumable> Restores 80 Health and 50 Mana over 10 seconds.', 2009),
(348, 'Hunter\'s Talisman', 'Provides damage against Monsters and Mana Regen in the Jungle', '<stats><mana>+150% Base Mana Regen while in Jungle </mana></stats><br><br><unique>UNIQUE Passive - Tooth:</unique> Damaging a monster with a spell or attack steals 25 Health over 5 seconds. Killing monsters grants <font color=\'#99BBBB\'><a href=\'SpecialJungleExperience\'>special bonus experience</a></font>.', 1039),
(349, 'B. F. Sword', 'Greatly increases Attack Damage', '<stats>+40 Attack Damage</stats>', 1038),
(350, 'Pickaxe', 'Moderately increases Attack Damage', '<stats>+25 Attack Damage</stats>', 1037),
(351, 'Long Sword', 'Slightly increases Attack Damage', '<stats>+10 Attack Damage</stats>', 1036),
(352, 'Health Potion', 'Consume to restore Health over time', '<groupLimit>Limited to 5 at one time. Limited to 1 type of Healing Potion.</groupLimit><br><br><consumable>Click to Consume:</consumable> Restores 150 Health over 15 seconds.', 2003),
(353, 'Head of Kha\'Zix', '', '<unique>UNIQUE Passive - Mementos of the Hunt:</unique> Rengar collects trophies when killing Champions and gains bonus effects based on how many trophies he has. Kills and assists grant 1 trophy.<br><br><passive>3 Trophies:</passive> Rengar gains 25 Movement Speed whilst out of combat or in brush. <br><passive>6 Trophies:</passive> Increases the range of Rengar\'s Leap by 125.<br><passive>12 Trophies:</passive> Thrill of the Hunt\'s duration is increased by 5 seconds.<br><passive>20 Trophies:</passive> Thrill of the Hunt\'s Movement Speed while stealthed is doubled.', 3455),
(354, 'Doran\'s Blade', 'Good starting item for attackers', '<stats>+8 Attack Damage<br>+80 Health<br>+3% Life Steal</stats>', 1055),
(355, 'Guardian\'s Orb', 'Good starting item for mages', '<stats>+150 Health<br>+35 Ability Power<br><mana>+10 Mana regen per 5 seconds</mana></stats><br><br><groupLimit>Limited to 1 Guardian\'s Item.</groupLimit>', 3112),
(356, 'Doran\'s Shield', 'Good defensive starting item', '<stats>+80 Health</stats><br><br><passive>Passive: </passive>Restores 6 Health every 5 seconds.<br><passive>Passive: </passive>Basic attacks deal an additional 5 physical damage to minions on hit.<br><unique>UNIQUE Passive:</unique> Regain an additional 20 health over 10 seconds after taking damage from an enemy champion.', 1054),
(357, 'Mercury\'s Treads', 'Increases Movement Speed and reduces duration of disabling effects', '<stats>+25 Magic Resist</stats><br><br><unique>UNIQUE Passive - Enhanced Movement:</unique> +45 Movement Speed<br><unique>UNIQUE Passive - Tenacity:</unique> Reduces the duration of stuns, slows, taunts, fears, silences, blinds, polymorphs, and immobilizes by 30%.', 3111),
(358, 'Vampiric Scepter', 'Basic attacks restore Health', '<stats>+15 Attack Damage<br>+10% Life Steal</stats>', 1053),
(359, 'Frozen Heart', 'Massively increases Armor and slows enemy basic attacks', '<stats>+100 Armor<br>+20% Cooldown Reduction<br><mana>+400 Mana</mana></stats><br><br><aura>UNIQUE Aura:</aura> Reduces the Attack Speed of nearby enemies by 15%.', 3110),
(360, 'Amplifying Tome', 'Slightly increases Ability Power', '<stats>+20 Ability Power</stats>', 1052),
(361, 'Brawler\'s Gloves', 'Slightly increases Critical Strike Chance', '<stats>+10% Critical Strike Chance</stats>', 1051),
(362, 'Elixir of Wrath', 'Temporarily grants Attack Damage and heals you when dealing physical damage to champions.', '<stats><levelLimit>Level 9 required to purchase.</levelLimit></stats><br><br><consumable>Click to Consume:</consumable> Grants +30 Attack Damage and <font color=\'#FF8811\'><u>Bloodlust</u></font> for 3 minutes.<br><br><font color=\'#FF8811\'><u>Bloodlust:</u></font> Dealing physical damage to champions heals for 15% of the damage dealt.<br><br><rules>(Only one Elixir effect may be active at a time.)</rules>', 2140),
(363, 'Knight\'s Vow', 'Partner with an ally to protect each other', '<stats>+250 Health<br>+40 Armor<br>+10% Cooldown Reduction</stats><br><br><active>UNIQUE Active:</active> Designate an allied champion as your <a href=\'KnightsVowPartner\'>Partner</a> (90 second cooldown).<br><passive>UNIQUE Passive:</passive> If your <a href=\'KnightsVowPartner\'>Partner</a> is nearby, gain +20 additional Armor and +15% Movement Speed towards them.<br><passive>UNIQUE Passive:</passive> If your <a href=\'KnightsVowPartner\'>Partner</a> is nearby, heal for 12% of the damage your <a href=\'KnightsVowPartner\'>Partner</a> deals to champions and redirect 12% of the damage your <a href=\'KnightsVowPartner\'>Partner</a> takes from champions to you as <font color=\'#FFFFFF\'>true</font> damage (healing and damage redirection are reduced by 50% if you are ranged).<br><br><rules>(Champions can only be linked by one Knight\'s Vow at a time.)</rules>', 3109),
(364, 'Death\'s Daughter', 'Cannon Barrage fires a mega-cannonball', 'Requires 500 Silver Serpents.<br><br><unique>UNIQUE Passive:</unique> Cannon Barrage additionally fires a mega-cannonball at center of the Barrage, dealing 300% true damage and slowing them by 60% for 1.5 seconds. ', 3902),
(365, 'Fiendish Codex', 'Increases Ability Power and Cooldown Reduction', '<stats>+30 Ability Power</stats><br><br><unique>UNIQUE Passive:</unique> +10% Cooldown Reduction', 3108),
(366, 'Raise Morale', 'Cannon Barrage hastes allies', 'Requires 500 Silver Serpents.<br><br><unique>UNIQUE Passive:</unique> Allies in the Cannon Barrage gain 30% Movement Speed for 2 seconds.', 3903),
(367, 'Elixir of Sorcery', 'Temporarily grants Ability Power and Bonus Damage to champions and turrets.', '<stats><levelLimit>Level 9 required to purchase.</levelLimit></stats><br><br><consumable>Click to Consume:</consumable> Grants +50 Ability Power, 15 bonus Mana Regen per 5 seconds and <font color=\'#FF8811\'><u>Sorcery</u></font> for 3 minutes. <br><br><font color=\'#FF8811\'><u>Sorcery:</u></font> Damaging a champion or turret deals 25 bonus True Damage. This effect has a 5 second cooldown versus champions but no cooldown versus turrets.<br><br><rules>(Only one Elixir effect may be active at a time.)</rules><br>', 2139),
(368, 'Redemption', 'Activate to heal allies and damage enemies in an area', '<stats>+200 Health<br>+50% Base Health Regen <br><mana>+150% Base Mana Regen </mana><br>+10% Cooldown Reduction</stats><br><br><passive>UNIQUE Passive:</passive> +10% Heal and Shield Power<br><active>UNIQUE Active:</active> Target an area within 5500 range. After 2.5 seconds, call down a beam of light to heal allies for 10 (+20 per level of target) Health, burn enemy champions for 10% of their maximum Health as <font color=\'#FFFFFF\'>true</font> damage and deal 250 <font color=\'#FFFFFF\'>true</font> damage to enemy minions (120 second cooldown). Heal and Shield Power is 3 times as effective on Redemption\'s heal.<br><br>Can be used while dead.<br><br><rules>Half effect if the target has been affected by another Redemption recently.</rules>', 3107),
(369, 'Elixir of Iron', 'Temporarily increases defenses. Leaves a trail for allies to follow.', '<stats><levelLimit>Level 9 required to purchase.</levelLimit></stats><br><br><consumable>Click to Consume:</consumable> Grants +300 Health, 25% Tenacity, increased champion size, and <font color=\'#FF8811\'><u>Path of Iron</u></font> for 3 minutes.<br><br><font color=\'#FF8811\'><u>Path of Iron:</u></font> Moving leaves a path behind that boosts allied champion\'s Movement Speed by 15%.<br><br><rules>(Only one Elixir effect may be active at a time.)</rules>', 2138),
(370, 'Arcane Sweeper', 'Activate to reveal a nearby area of the map', '<active>UNIQUE Active - Hunter\'s Sight:</active> An arcane mist grants vision in the target area for 5 seconds, revealing enemy champions and granting <font color=\'#ee91d7\'>True Sight</font> of traps in the area for 3 seconds (90 second cooldown).', 3348),
(371, 'Aegis of the Legion', 'Grants Armor and Magic Resistance', '<stats>+30 Armor<br>+30 Magic Resist</stats>', 3105),
(372, 'Kircheis Shard', 'Attack speed and a chargable magic hit', '<stats>+15% Attack Speed</stats><br><br><passive>Passive:</passive> Moving and attacking will make an attack <a href=\'Energized\'>Energized</a>.<br><br><unique>UNIQUE Passive - Energized Strike:</unique> Your Energized attacks deal 50 bonus magic damage on hit.', 2015),
(373, 'Lord Van Damm\'s Pillager', 'Reduces Armor of nearby enemies', '<stats>+300 Health<br>+50 Attack Damage<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive - Ashes to Ashes:</unique> Controlling the nearest Altar sets you aflame, dealing 25 (+1 per champion level) magic damage per second to nearby enemies (Deals 50% bonus damage to minions and monsters). Controlling the furthest Altar causes your basic attacks to burn targets for up to 114 true damage (based on champion level) over 3 seconds.', 3104),
(374, 'Soul Anchor (Trinket)', 'Consumes charge to revive champion.', '<groupLimit>Limited to 1 Trinket.</groupLimit><br><br><active>Active:</active> Consumes a charge to instantly revive at your Summoner Platform and grants 125% Movement Speed that decays over 12 seconds.<br><br><rules>Additional charges are gained at levels 9 and 14.</rules><br><br><font color=\'#BBFFFF\'>(Max: 2 charges)</font></rules><br><br>', 3345),
(375, 'Banshee\'s Veil', 'Periodically blocks enemy abilities', '<stats>+70 Ability Power<br>+60 Magic Resist<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive:</unique> Grants a spell shield that blocks the next enemy ability. This shield refreshes after no damage is taken from enemy champions for 40 seconds.', 3102),
(376, 'Fire at Will', 'Cannon Barrage gains extra waves', 'Requires 500 Silver Serpents.<br><br><unique>UNIQUE Passive:</unique> Cannon Barrage fires at an increasing rate over time (additional 6 waves over the duration).', 3901),
(377, 'Executioner\'s Calling', 'Overcomes enemies with high health gain', '<stats>+15 Attack Damage</stats><br><br><unique>UNIQUE Passive - Executioner:</unique> Physical damage inflicts <a href=\'GrievousWounds\'>Grievous Wounds</a> on enemy champions for 3 seconds.', 3123),
(378, 'Corrupting Potion', 'Restores Health and Mana over time and boosts combat power - Refills at Shop', '<groupLimit>Limited to 1 type of Healing Potion.</groupLimit><br><br><active>UNIQUE Active:</active> Consumes a charge to restore 125 Health and 75 Mana over 12 seconds and grants <font color=\'#FF8811\'><u>Touch of Corruption</u></font> during that time. Holds up to 3 charges that refills upon visiting the shop.<br><br><font color=\'#FF8811\'><u>Touch of Corruption:</u></font> Damaging spells and attacks burn enemy champions for <scaleLevel>15 - 30</scaleLevel> magic damage over 3 seconds. (Half Damage for Area of Effect or Damage over Time spells. Damage increases with champion level.)<br><br><rules>(Corrupting Potion can be used even at full Health and Mana.)</rules>', 2033),
(379, 'Abyssal Mask', 'Nearby enemies take more magic damage', '<stats>+350 Health<br><mana>+300 Mana</mana><br>+55 Magic Resist<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive - Eternity:</unique> 15% of damage taken from champions is gained as Mana. Spending Mana restores 20% of the cost as Health, up to 25 per spell cast.<br><aura>UNIQUE Aura:</aura> Nearby enemy champions take 10% more magic damage.', 3001),
(380, 'Wicked Hatchet', 'Critical Strikes cause your target to bleed', '<stats>+20 Attack Damage<br>+10% Critical Strike Chance</stats><br><br><unique>UNIQUE Passive:</unique> Critical Strikes cause your target to bleed for an additional 60% of your bonus Attack Damage as magic damage over 3 seconds.', 3122),
(381, 'Oracle Alteration', 'Disables nearby invisible wards and traps for a duration', '<levelLimit>* Level 9+ required to upgrade.</levelLimit><stats></stats><br><br>Alters the <font color=\'#FFFFFF\'>Sweeping Lens</font> Trinket:<br><br><stats><font color=\'#00FF00\'>+</font> Increased detection radius<br><font color=\'#00FF00\'>+</font> Sweeping effect follows you for 10 seconds<br><font color=\'#FF0000\'>-</font> <font color=\'#FF6600\'>Cast range reduced to zero</font></stats>', 3364),
(382, 'Hunter\'s Potion', 'Restores Health and Mana over time - Refills at shop and has increased capacity', '<groupLimit>Limited to 1 type of Healing Potion.</groupLimit><br><br><active>UNIQUE Active:</active> Consumes a charge to restore 60 Health and 35 Mana over 8 seconds. Holds up to 5 charges and refills upon visiting the shop.<br><br>Killing a Large Monster grants 1 charge.<br><br><rules>(Killing a Large Monster at full charges will automatically consume the newest charge.)</rules>', 2032),
(383, 'Farsight Alteration', 'Grants increased range and reveals the targetted area', '<levelLimit>* Level 9+ required to upgrade.</levelLimit><br><br>Alters the <font color=\'#FFFFFF\'>Warding Totem</font> Trinket:<br><br><stats><font color=\'#00FF00\'>+</font> Massively increased cast range (+650%)<br><font color=\'#00FF00\'>+</font> Infinite duration and does not count towards ward limit<br><font color=\'#FF0000\'>-</font> <font color=\'#FF6600\'>10% increased cooldown</font><br><font color=\'#FF0000\'>-</font> <font color=\'#FF6600\'>Ward is visible, fragile, untargetable by allies</font><br><font color=\'#FF0000\'>-</font> <font color=\'#FF6600\'>45% reduced ward vision radius</font><br><font color=\'#FF0000\'>-</font> <font color=\'#FF6600\'>Cannot store charges</font></stats>', 3363),
(384, 'Refillable Potion', 'Restores Health over time. Refills at shop.', '<groupLimit>Limited to 1 type of Healing Potion.</groupLimit><br><br><active>UNIQUE Active:</active> Consumes a charge to restore 125 Health over 12 seconds. Holds up to 2 charges and refills upon visiting the shop.', 2031),
(385, 'Greater Vision Totem (Trinket)', 'Periodically place a Vision Ward', '<groupLimit>Limited to 1 Trinket.</groupLimit><levelLimit> *Level 9+ required to upgrade.</levelLimit><stats></stats><br><br><unique>UNIQUE Active:</unique> Places a visible ward that reveals the surrounding area and invisible units in the area until killed (120 second cooldown). Limit 1 <font color=\'#BBFFFF\'>Vision Ward</font> on the map per player.<br><br><rules>(Trinkets cannot be used in the first 30 seconds of a game. Selling a Trinket will disable Trinket use for 120 seconds).</rules>', 3362),
(386, 'Greater Stealth Totem (Trinket)', 'Periodically place a Stealth Ward', '<groupLimit>Limited to 1 Trinket.</groupLimit><levelLimit> *Level 9+ required to upgrade.</levelLimit><stats></stats><br><br><unique>UNIQUE Active:</unique> Consume a charge to place an invisible ward that reveals the surrounding area for 180 seconds. Stores a charge every 60 seconds, up to 2 total. Limit 3 <font color=\'#BBFFFF\'>Stealth Wards</font> on the map per player.<br><br><rules>(Trinkets cannot be used in the first 30 seconds of a game. Selling a Trinket will disable Trinket use for 120 seconds).</rules>', 3361),
(387, 'Boots of Mobility', 'Greatly enhances Movement Speed when out of combat', '<unique>UNIQUE Passive - Enhanced Movement:</unique> +25 Movement Speed. Increases to +115 Movement Speed when out of combat for 5 seconds.', 3117),
(388, 'Rylai\'s Crystal Scepter', 'Abilities slow enemies', '<stats>+300 Health<br>+75 Ability Power</stats><br><br><unique>UNIQUE Passive:</unique> Damaging spells and abilities reduce enemy movement speed by 20% for 1 second.', 3116),
(389, 'Needlessly Large Rod', 'Greatly increases Ability Power', '<stats>+60 Ability Power</stats>', 1058),
(390, 'Nashor\'s Tooth', 'Increases Attack Speed, Ability Power, and Cooldown Reduction', '<stats>+50% Attack Speed<br>+80 Ability Power</stats><br><br><unique>UNIQUE Passive:</unique> +20% Cooldown Reduction<br><unique>UNIQUE Passive:</unique> Basic attacks deal 15 (+15% of Ability Power) bonus magic damage on hit.<br>', 3115),
(391, 'The Black Spear', 'Kalista\'s spear that binds an Oathsworn Ally.', '<stats></stats><br><active>Active:</active> Offer to bind with an ally for the remainder of the game, becoming Oathsworn Allies. Oathsworn empowers you both while near one another.', 3599),
(392, 'Negatron Cloak', 'Moderately increases Magic Resist', '<stats>+40 Magic Resist</stats>', 1057),
(393, 'Forbidden Idol', 'Increases Mana Regeneration and Cooldown Reduction', '<stats><mana>+50% Base Mana Regen </mana></stats><br><br><unique>UNIQUE Passive:</unique> +10% Cooldown Reduction<br><unique>UNIQUE Passive:</unique> +8% Heal and Shield Power', 3114),
(394, 'Doran\'s Ring', 'Good starting item for casters', '<stats>+60 Health<br>+15 Ability Power<br><mana>+50% Base Mana Regen </mana></stats><br><br><mana><passive>UNIQUE Passive:</passive> Restores 4 Mana upon killing a unit.</mana>', 1056),
(395, 'Aether Wisp', 'Increases Ability Power and Movement Speed', '<stats>+30 Ability Power</stats><br><br><unique>UNIQUE Passive:</unique> +5% Movement Speed', 3113);
INSERT INTO `lol_items` (`id`, `name`, `plaintext`, `description`, `riot_id`) VALUES
(396, 'Ruby Sightstone', 'Greatly increases Health and provides Stealth Wards over time', '<stats>+500 Health</stats><br><br><unique>UNIQUE Passive:</unique> Item Active cooldowns are reduced by 20%.<br><active>UNIQUE Active - Warding:</active> Consumes a charge to place a <font color=\'#BBFFFF\'>Stealth Ward</font> that reveals the surrounding area for 150 seconds. Holds up to 4 charges and refills when visiting the shop.<br><br><rules>(A player may only have 3 <font color=\'#BBFFFF\'>Stealth Wards</font> on the map at one time. Unique Passives with the same name don\'t stack.)</rules>', 2045),
(397, 'Serrated Dirk', 'Increases Attack Damage and Lethality', '<stats>+25 Attack Damage</stats><br><br><unique>UNIQUE Passive:</unique> +10 <a href=\'Lethality\'>Lethality</a><br><unique>UNIQUE Passive - Headhunter:</unique> After killing any enemy, your next damaging spell will deal 40 bonus physical damage (30 second cooldown).', 3134),
(398, 'Caulfield\'s Warhammer', 'Attack Damage and Cooldown Reduction', '<stats>+25 Attack Damage</stats><br><br><unique>UNIQUE Passive:</unique> +10% Cooldown Reduction', 3133),
(399, 'Rabadon\'s Deathcrown', 'Massively increases Ability Power', '<stats><font color=\'#FFFFFF\'>+175 Ability Power</font> </stats><br><br><unique>UNIQUE Passive:</unique> Increases Ability Power by 35%.', 3374),
(400, 'Catalyst of Aeons', 'Spend Mana to recover Health', '<stats>+225 Health<br><mana>+300 Mana</mana></stats><br><br><unique>UNIQUE Passive - Eternity:</unique> 15% of damage taken from champions is gained as Mana. <br><br>Spending Mana restores 20% of the cost as Health, up to 15 per spell cast. <br><br><rules>(Toggled Spells heal for a maximum of 15 per second.)</rules>', 3010),
(401, 'Poacher\'s Dirk', 'Transforms into a Serrated Dirk after poaching in the enemy jungle.', '<stats>+10 Attack Damage</stats><br><br><unique>UNIQUE Passive - Headhunter:</unique> After killing any enemy, your next damaging spell will deal 40 bonus physical damage (30 second cooldown).<br><unique>UNIQUE Passive:</unique> After poaching 3 large monsters from the enemy jungle (40 second cooldown), transforms into a Serrated Dirk.', 3252),
(402, 'Forgefire Cape', 'Constantly deals damage to nearby enemies', '<stats><font color=\'#FFFFFF\'>+625 Health</font><br><font color=\'#FFFFFF\'>+90 Armor</font></stats><br><br><unique>UNIQUE Passive - Immolate:</unique> Deals 11 (+1 per champion level) magic damage per second to nearby enemies. Deals 200% bonus damage to minions and monsters.', 3373),
(403, 'Molten Edge', 'Massively enhances critical strikes', '<stats><font color=\'#FFFFFF\'>+100 Attack Damage</font><br>+20% Critical Strike Chance</stats><br><br><unique>UNIQUE Passive:</unique> Critical strike bonus damage is increased by 50%.', 3371),
(404, 'Boots of Swiftness', 'Enhances Movement Speed and reduces the effect of slows', '<unique>UNIQUE Passive - Enhanced Movement:</unique> +55 Movement Speed<br><unique>UNIQUE Passive - Slow Resist:</unique> Movement slowing effects are reduced by 25%.', 3009),
(405, 'Manamune (Quick Charge)', 'Increases Attack Damage based on maximum Mana', '<stats>+25 Attack Damage<br><mana>+250 Mana</mana></stats><br><br><mana><unique>UNIQUE Passive - Awe:</unique> Grants bonus Attack Damage equal to 2% of maximum Mana. Refunds 15% of Mana spent.<br><unique>UNIQUE Passive - Mana Charge:</unique> Grants +6 maximum Mana (max +750 Mana) for each basic attack, spell cast or Mana expenditure (occurs up to 3 times every 12 seconds).<br><br>Transforms into Muramana at +750 Mana.</mana>', 3008),
(406, 'Archangel\'s Staff (Quick Charge)', 'Increases Ability Power based on maximum Mana', '<stats>+80 Ability Power<br><mana>+250 Mana</mana></stats><br><br><mana><unique>UNIQUE Passive - Awe:</unique> Grants Ability Power equal to 3% of maximum Mana. Refunds 25% of Mana spent. <br><unique>UNIQUE Passive - Mana Charge:</unique> Grants +12 maximum Mana (max +750 Mana) for each spell cast or Mana expenditure (occurs up to 3 times every 12 seconds).<br><br>Transforms into Seraph\'s Embrace at +750 Mana.</mana>', 3007),
(407, 'Berserker\'s Greaves', 'Enhances Movement Speed and Attack Speed', '<stats> +35% Attack Speed</stats><br><br><unique>UNIQUE Passive - Enhanced Movement:</unique> +45 Movement Speed', 3006),
(408, 'Manamune', 'Increases Attack Damage based on maximum Mana', '<stats>+25 Attack Damage<br><mana>+250 Mana</mana></stats><br><br><mana><unique>UNIQUE Passive - Awe:</unique> Grants bonus Attack Damage equal to 2% of maximum Mana. Refunds 15% of Mana spent.<br><unique>UNIQUE Passive - Mana Charge:</unique> Grants +5 maximum Mana (max +750 Mana) for each basic attack, spell cast or Mana expenditure (occurs up to 3 times every 12 seconds).<br><br>Transforms into Muramana at +750 Mana.</mana>', 3004),
(409, 'Archangel\'s Staff', 'Increases Ability Power based on maximum Mana', '<stats>+80 Ability Power<br><mana>+250 Mana</mana></stats><br><br><mana><unique>UNIQUE Passive - Awe:</unique> Grants Ability Power equal to 3% of maximum Mana. Refunds 25% of Mana spent.<br><unique>UNIQUE Passive - Mana Charge:</unique> Grants +8 maximum Mana (max +750 Mana) for each spell cast or Mana expenditure (occurs up to 3 times every 12 seconds).<br><br>Transforms into Seraph\'s Embrace at +750 Mana.</mana>', 3003),
(410, 'Guinsoo\'s Rageblade', 'Increases Ability Power and Attack Damage', '<stats>+35 Attack Damage<br>+50 Ability Power<br>+25% Attack Speed</stats><br><br><passive>Passive: </passive>Basic attacks deal an additional 15 magic damage on hit.<br><unique>UNIQUE Passive:</unique> Basic attacks grant +8% Attack Speed, +3 Attack Damage, and +4 Ability Power for 5 seconds (stacks up to 6 times). While you have 6 stacks, gain <unlockedPassive>Guinsoo\'s Rage</unlockedPassive>.<br><br><unlockedPassive>Guinsoo\'s Rage:</unlockedPassive> Every other basic attack will trigger on hit effects an additional time.', 3124),
(411, 'Righteous Glory', 'Grants Health, Mana, and Armor. Activate to speed towards enemies and slow them.', '<stats>+400 Health<br><mana>+300 Mana</mana><br>+30 Armor<br>+100% Base Health Regen <br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Active:</unique> Grants 75% Movement Speed when moving towards enemies or enemy turrets for 4 seconds. Once near an enemy (or after 4 seconds) a shockwave is emitted, slowing nearby enemy champion Movement Speed by 75% for 2 second(s) (90 second cooldown).', 3800),
(412, 'Crystalline Bracer', 'Grants Health and Health Regen', '<stats>+200 Health<br>+50% Base Health Regen </stats>', 3801),
(413, 'Lost Chapter', 'Restores Mana upon levelling up.', '<stats>+25 Ability Power<br><mana>+300 Mana</mana></stats><br><br><unique>UNIQUE Passive:</unique> Upon levelling up, restores 20% of your maximum Mana over 3 seconds.', 3802),
(414, 'Glacial Shroud', 'Increases Armor and Cooldown Reduction', '<stats>+20 Armor<br><mana>+250 Mana</mana></stats><br><br><unique>UNIQUE Passive:</unique> +10% Cooldown Reduction', 3024),
(415, 'Hextech Revolver', 'Increases Ability Power. Deal bonus magic damage on attack periodically.', '<stats>+40 Ability Power</stats><br><br><unique>UNIQUE Passive - Magic Bolt:</unique> Damaging an enemy champion with a basic attack shocks them for <scaleLevel>50 - 125</scaleLevel> bonus magic damage (40 second cooldown, shared with other <font color=\'#9999FF\'><a href=\'itembolt\'>Hextech</a></font> items).<br><br>Magic Bolt\'s cooldown is reduced by Active Item cooldown reduction.<br><br><rules>(Damage scales based on level. Hextech effects can trigger other item spell effects.)</rules>', 3145),
(416, 'Control Ward', 'Used to disable wards and invisible traps in an area.', '<groupLimit>Can only carry 3 Control Wards in inventory.</groupLimit><br><br><consumable>Click to Consume:</consumable> Places a ward that grants vision of the surrounding area. This device will also reveal invisible traps and reveal / disable wards. Control Wards do not disable other Control Wards. Camouflaged units will also be revealed. <br><br>Limit 1 <font color=\'#BBFFFF\'>Control Ward</font> on the map per player.', 2055),
(417, 'Bilgewater Cutlass', 'Activate to deal magic damage and slow target champion', '<stats>+25 Attack Damage<br>+10% Life Steal</stats><br><br><active>UNIQUE Active:</active> Deals 100 magic damage and slows the target champion\'s Movement Speed by 25% for 2 seconds (90 second cooldown).', 3144),
(418, 'Zhonya\'s Paradox', 'Activate to become invincible but unable to take actions', '<stats><font color=\'#FFFFFF\'>+100 Ability Power</font><br><font color=\'#FFFFFF\'>+60 Armor</font><br>+10% Cooldown Reduction</stats><br><br><active>UNIQUE Active - Stasis:</active> Champion becomes invulnerable and untargetable for 2.5 seconds, but is unable to move, attack, cast spells, or use items during this time (120 second cooldown).', 3386),
(419, 'Diet Poro-Snax', '', 'All the flavor of regular Poro-Snax, without the calories! Keeps your Poro happy AND healthy.<br><br><consumable>Click to Consume:</consumable> Gives your Poros a delicious healthy treat.', 2054),
(420, 'Frozen Mallet', 'Basic attacks slow enemies', '<stats>+700 Health<br>+30 Attack Damage</stats><br><br><unique>UNIQUE Passive - Icy:</unique> Basic attacks slow the target\'s Movement Speed for 1.5 seconds on hit (40% slow for melee attacks, 30% slow for ranged attacks).', 3022),
(421, 'Randuin\'s Omen', 'Greatly increases defenses, activate to slow nearby enemies', '<stats>+400 Health<br>+60 Armor</stats><br><br><unique>UNIQUE Passive:</unique> -20% damage taken from basic attack critical strikes.<br><unique>UNIQUE Passive - Cold Steel:</unique> When hit by basic attacks, reduces the attacker\'s Attack Speed by 15% for 1 second.<br><active>UNIQUE Active:</active> Slows the Movement Speed of nearby enemy units by 55% for 2 seconds (60 second cooldown).', 3143),
(422, 'Wooglet\'s Witchcrown', 'Massively increases Ability Power and can be activated to enter stasis', '<stats><font color=\'#FFFFFF\'>+155 Ability Power</font><br>+45 Armor </stats><br><br><unique>UNIQUE Passive:</unique> Increases Ability Power by 25%<br><active>UNIQUE Active:</active> Champion becomes invulnerable and untargetable for 2.5 seconds, but is unable to move, attack, cast spells, or use items during this time (120 second cooldown).', 3385),
(423, 'Raptor Cloak', 'Enhances Movement Speed near turrets', '<stats>+30 Armor<br>+125% Base Health Regen </stats><br><br><unique>UNIQUE Passive - Point Runner:</unique> Builds up to +20% Movement Speed over 2 seconds while near turrets, fallen turrets and Void Gates.', 2053),
(424, 'Youmuu\'s Ghostblade', 'Activate to greatly increase Movement Speed', '<stats>+55 Attack Damage<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive:</unique> +18 <a href=\'Lethality\'>Lethality</a><br><unique>UNIQUE Passive:</unique> +40 Movement Speed out of Combat<br><active>UNIQUE Active:</active> Grants +20% Movement Speed for 6 seconds (45 second cooldown).', 3142),
(425, 'Trinity Fusion', 'Tons of Damage', '<stats><font color=\'#FFFFFF\'>+350 Health</font><br><font color=\'#FFFFFF\'><mana>+350 Mana</mana></font><br><font color=\'#FFFFFF\'>+35 Attack Damage</font><br><font color=\'#FFFFFF\'>+50% Attack Speed</font><br>+20% Cooldown Reduction<br><font color=\'#FFFFFF\'>+8% Movement Speed</font></stats><br><br><unique>UNIQUE Passive - Rage:</unique> Basic attacks grant 20 Movement Speed for 2 seconds. Kills grant 60 Movement Speed instead. This Movement Speed bonus is halved for ranged champions.<br><unique>UNIQUE Passive - Spellblade:</unique> After using an ability, the next basic attack deals bonus physical damage equal to 200% of base Attack Damage on hit (1.5 second cooldown).', 3384),
(426, 'Poro-Snax', '', 'This savory blend of free-range, grass-fed Avarosan game hens and organic, non-ZMO Freljordian herbs contains the essential nutrients necessary to keep your Poro purring with pleasure.<br><br><i>All proceeds will be donated towards fighting Noxian animal cruelty.</i>', 2052),
(427, 'Sorcerer\'s Shoes', 'Enhances Movement Speed and magic damage', '<stats>+15 <a href=\'FlatMagicPen\'>Magic Penetration</a></stats><br><br><unique>UNIQUE Passive - Enhanced Movement:</unique> +45 Movement Speed', 3020),
(428, 'Circlet of the Iron Solari', 'Activate to shield nearby allies from damage', '<stats><font color=\'#FFFFFF\'>+45 Armor</font><br><font color=\'#FFFFFF\'>+75 Magic Resist</font></stats><br><br><active>UNIQUE Active:</active> Grants a decaying shield to nearby allied champions for 2.5 seconds that absorbs up to 35 (+22 per level) <scaleHealth>(+2%~36% of your bonus health)</scaleHealth> damage (90 second cooldown).<br><br><rules>Shield per level uses the higher of your level or the target\'s level.<br>Shield ratio scales with your level.<br>Shield amount is reduced to 0% if the target has been affected by another Locket of the Iron Solari in the last 5 seconds.</rules>', 3383),
(429, 'Cull', 'Provides damage and Life Steal on hit - Killing minions grant bonus Gold', '<stats>+7 Attack Damage<br>+3 Life on Hit</stats><br><br><unique>UNIQUE Passive:</unique> Killing a lane minion grants 1 additional Gold. Killing 100 lane minions grants an additional 350 bonus gold immediately and disables this passive.', 1083),
(430, 'Guardian\'s Horn', 'Good starting item for tanks', '<stats>+150 Health</stats><br><br><passive>Passive: </passive>Restores 20 Health every 5 seconds.<br><unique>UNIQUE Passive:</unique> Blocks 12 damage from attacks and spells from champions (25% effectiveness vs. damage over time abilities).<br><br><groupLimit>Limited to 1 Guardian\'s Item.</groupLimit>', 2051),
(431, 'Quicksilver Sash', 'Activate to remove all crowd control debuffs', '<stats>+30 Magic Resist</stats><br><br><active>UNIQUE Active - Quicksilver:</active> Removes all crowd control debuffs (90 second cooldown).', 3140),
(432, 'Salvation', 'Activate to heal allies and damage enemies in an area', '<stats><font color=\'#FFFFFF\'>+300 Health</font><br><font color=\'#FFFFFF\'>+150% Base Health Regen </font><br><font color=\'#FFFFFF\'>+200% Base Mana Regen </font><br>+10% Cooldown Reduction</stats><br><br><passive>UNIQUE Passive:</passive> +10% Heal and Shield Power<br><active>UNIQUE Active:</active> Target an area within 5500 range. After 2.5 seconds, call down a beam of light to heal allies for 10 (+20 per level of target) Health, burn enemy champions for 10% of their maximum Health as <font color=\'#FFFFFF\'>true</font> damage and deal 250 <font color=\'#FFFFFF\'>true</font> damage to enemy minions (120 second cooldown). Heal and Shield Power is 3 times as effective on Salvation\'s heal.<br><br>Can be used while dead.<br><br><rules>Half effect if the target has been affected by Redemption recently.</rules>', 3382),
(433, 'The Dark Seal', 'Provides Ability Power and Mana. Increases in power as you kill enemies.', '<stats>+15 Ability Power<br>+25% Increased Healing from Potions<br><mana>+100 Mana</mana></stats><br><br><unique>UNIQUE Passive - Dread:</unique> Grants +3 Ability Power per Glory. <br><unique>UNIQUE Passive - Do or Die:</unique> Grants 2 Glory for a champion kill or 1 Glory for an assist, up to 10 Glory total. Lose 4 Glory on death.', 1082),
(434, 'Explorer\'s Ward', '', '<consumable>Click to Consume:</consumable> Places an invisible ward that reveals the surrounding area for 60 seconds.', 2050),
(435, 'The Obsidian Cleaver', 'Dealing physical damage to enemy champions reduces their Armor', '<stats><font color=\'#FFFFFF\'>+550 Health</font><br><font color=\'#FFFFFF\'>+60 Attack Damage</font><br>+20% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive:</unique> Dealing physical damage to an enemy champion Cleaves them, reducing their Armor by 4% for 6 seconds (stacks up to 6 times, up to 24%).<br><unique>UNIQUE Passive - Rage:</unique> Dealing physical damage grants 20 movement speed for 2 seconds. Assists on Cleaved enemy champions or kills on any unit grant 60 movement speed for 2 seconds instead. This Movement Speed is halved for ranged champions.', 3380),
(436, 'Edge of Night', 'Blocks an incoming enemy spell.', '<stats>+250 Health<br>+55 Attack Damage</stats><br><br><unique>UNIQUE Passive:</unique> +18 <a href=\'Lethality\'>Lethality</a><br><unique>UNIQUE Active - Night\'s Veil:</unique> Channel for 1.5 second to grant a spell shield that blocks the next enemy ability. Lasts for 7 seconds (40 second cooldown).<br><br><rules>(Can move while channeling, but taking damage breaks the channel.)</rules>', 3814),
(437, 'Mercurial Scimitar', 'Activate to remove all crowd control debuffs and grant massive Movement Speed', '<stats>+65 Attack Damage<br>+35 Magic Resist<br>+10% Life Steal</stats><br><br><active>UNIQUE Active - Quicksilver:</active> Removes all crowd control debuffs and also grants +50% bonus Movement Speed for 1 second (90 second cooldown).', 3139),
(438, 'Sightstone', 'Increases Health and provides Stealth Wards over time', '<stats>+150 Health</stats><br><br><active>UNIQUE Active - Warding:</active> Consumes a charge to place a <font color=\'#BBFFFF\'>Stealth Ward</font> that reveals the surrounding area for 150 seconds. Holds up to 3 charges which refill upon visiting the shop. <br><br><rules>(A player may only have 3 <font color=\'#BBFFFF\'>Stealth Wards</font> on the map at one time. Unique Passives with the same name don\'t stack.)</rules>', 2049),
(439, 'Dervish Blade', 'Activate to remove all debuffs and grant massive Movement Speed', '<stats>+50% Attack Speed<br>+45 Magic Resist<br>+10% Cooldown Reduction</stats><br><br><active>UNIQUE Active - Quicksilver:</active> Removes all debuffs, and if the champion is melee, also grants +50% bonus Movement Speed for 1 second (90 second cooldown).', 3137),
(440, 'Infernal Mask', 'Nearby enemies take more magic damage', '<stats><font color=\'#FFFFFF\'>+550 Health</font><br><mana>+300 Mana</mana><br><font color=\'#FFFFFF\'>+90 Magic Resist</font><br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive - Eternity:</unique> 15% of damage taken from champions is gained as Mana. Spending Mana restores 20% of the cost as Health, up to 25 per spell cast.<br><aura>UNIQUE Aura:</aura> Nearby enemy champions take 10% more magic damage.', 3379),
(441, 'Oracle\'s Extract', 'Allows champion to see invisible or unseen enemy units', '<consumable>Click to Consume:</consumable> Grants detection of nearby invisible or unseen enemy units for 5 minutes.', 2047),
(442, 'Haunting Guise', 'Increases magic damage', '<stats>+25 Ability Power<br>+200 Health</stats><br><br><unique>UNIQUE Passive - Eyes of Pain:</unique> +15 <a href=\'FlatMagicPen\'>Magic Penetration</a>', 3136),
(443, 'Void Staff', 'Increases magic damage', '<stats>+80 Ability Power</stats><br><br><unique>UNIQUE Passive:</unique> +35% <a href=\'TotalMagicPen\'>Magic Penetration</a>.', 3135),
(444, 'Death\'s Dance', 'Trades incoming damage now for incoming damage later', '<stats>+80 Attack Damage<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive:</unique> Dealing physical damage heals for 15% of the damage dealt. This is 33% as effective for Area of Effect damage.<br><unique>UNIQUE Passive:</unique> 30% of damage taken is dealt as a Bleed effect over 3 seconds instead.', 3812),
(445, 'Last Whisper', 'Overcomes enemies with high Armor', '<stats>+10 Attack Damage</stats><br><br><unique>UNIQUE Passive - Last Whisper:</unique> +35% <a href=\'BonusArmorPen\'>Bonus Armor Penetration</a>', 3035),
(446, 'Maw of Malmortius', 'Grants bonus Attack Damage when Health is low', '<stats>+50 Attack Damage<br>+45 Magic Resist<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Passive - Lifeline:</unique> Upon taking magic damage that would reduce Health below 30%, grants a shield that absorbs magic damage equal to 300 + 1 per bonus Magic Resistance for 5 seconds (90 second cooldown).<br><unlockedPassive>Lifegrip:</unlockedPassive> When <i>Lifeline</i> triggers, gain +20 Attack Damage, +10% Spell Vamp and +10% Life Steal until out of combat.', 3156),
(447, 'Giant Slayer', 'Overcomes enemies with high Health', '<stats>+10 Attack Damage</stats><br><br><unique>UNIQUE Passive - Giant Slayer:</unique> Grants up to +10% physical damage against enemy champions with greater maximum Health than you (+1% damage per 100 Health difference, maxing at 1000 Health difference).<br><br><rules>(Unique Passives with the same name don\'t stack.)</rules>', 3034),
(448, 'Hexdrinker', 'Increases Attack Damage and Magic Resist', '<stats>+20 Attack Damage<br>+35 Magic Resist</stats><br><br><unique>UNIQUE Passive - Lifeline:</unique> Upon taking magic damage that would reduce Health below 30%, grants a shield that absorbs 110 to 280 (based on level) magic damage for 5 seconds (90 second cooldown).', 3155),
(449, 'Mortal Reminder', 'Overcomes enemies with high Health recovery and Armor', '<stats>+50 Attack Damage</stats><br><br><unique>UNIQUE Passive - Executioner:</unique> Physical damage inflicts <a href=\'GrievousWounds\'>Grievous Wounds</a> on enemy champions for 5 seconds.<br><unique>UNIQUE Passive - Last Whisper:</unique> +35% <a href=\'BonusArmorPen\'>Bonus Armor Penetration</a>.', 3033),
(450, 'Blade of the Ruined King', 'Deals damage based on target\'s Health, can steal Movement Speed', '<stats>+40 Attack Damage<br>+25% Attack Speed<br>+12% Life Steal</stats><br><br><unique>UNIQUE Passive:</unique> Basic attacks deal 8% of the target\'s current Health as bonus physical damage on hit.<br><active>UNIQUE Active:</active> Deals 100 magic damage to target champion and steals 25% of their Movement Speed for 3 seconds (90 second cooldown).<br><br><font size=\'14\' color=\'#8c8c8c\'>Minimum bonus physical damage dealt is 15.<br>Maximum bonus physical damage dealt to monsters and minions is 60.<br>User\'s Life Steal is applied to bonus physical damage dealt.</font>', 3153),
(451, 'Infinity Edge', 'Massively enhances critical strikes', '<stats>+70 Attack Damage<br>+20% Critical Strike Chance</stats><br><br><unique>UNIQUE Passive:</unique> Critical strike bonus damage is increased by 50%.', 3031),
(452, 'Hextech Protobelt-01', 'Activate to dash forward and unleash a fiery explosion', '<stats>+300 Health<br>+60 Ability Power<br>+10% Cooldown Reduction</stats><br><br><unique>UNIQUE Active - Fire Bolt:</unique> Dash forward and unleash a nova of fire bolts that deal <scaleLevel>75 - 150</scaleLevel> (+25% of your Ability Power) as magic damage (40 second cooldown, shared with other <font color=\'#9999FF\'><a href=\'itembolt\'>Hextech</a></font> items).<br><br>Champions and Monsters hit by multiple fire bolts take 10% damage per additional bolt.<br><br><rules>(This dash cannot pass through terrain.)</rules>', 3152),
(453, 'Hextech GLP-800', 'Activate to fire icy bolts to slow enemies', '<stats>+300 Health<br><mana>+400 Mana</mana><br>+80 Ability Power</stats><br><br><unique>UNIQUE Passive - Eternity:</unique> 15% of damage taken from champions is gained as Mana. Spending Mana restores 20% of the cost as Health, up to 25 per spell cast.<br><unique>UNIQUE Active - Frost Bolt:</unique> Fires a spray of icy bolts that explode, dealing <scaleLevel>100 - 200</scaleLevel> (+35% of your Ability Power) magic damage to all enemies hit. (40 second cooldown, shared with other <font color=\'#9999FF\'><a href=\'itembolt\'>Hextech</a></font> items).<br><br>Enemies hit are slowed by 65% decaying over 0.5 seconds.<br><br><rules>(Frost Bolt has a cast time, in contrast to most actives.)</rules> ', 3030),
(454, 'Liandry\'s Torment', 'Spell damage burns enemies for a portion of their Health', '<stats>+80 Ability Power<br>+300 Health</stats><br><br><unique>UNIQUE Passive - Eyes of Pain:</unique> +15 <a href=\'FlatMagicPen\'>Magic Penetration</a><br><unique>UNIQUE Passive:</unique> Spells burn enemies for 3 seconds, dealing bonus magic damage equal to 2% of their current Health per second. Burn damage is doubled against <a href=\'MovementImpaired\'>movement-impaired</a> units.', 3151),
(455, 'Enchantment: Runic Echoes', 'Grants Ability Power and periodically empowers your Spells', '<stats>+60 Ability Power<br>+7% Movement Speed</stats><br><br><unique>UNIQUE Passive - Echo:</unique> Gain charges upon moving or casting. At 100 charges, the next damaging spell hit expends all charges to deal 60 (+10% of Ability Power) bonus magic damage to up to 4 targets on hit.<br><br>This effect deals 250% damage to Large Monsters. Hitting a Large Monster with this effect will restore 15% of your missing Mana.', 1402),
(456, 'Stalker\'s Blade', 'Lets your Smite slow Champions', '<groupLimit>Limited to 1 Jungle item</groupLimit><br><br><stats>+10% Life Steal vs. Monsters<br><mana>+225% Base Mana Regen while in Jungle</mana></stats><br><br><unique>UNIQUE Passive - Chilling Smite:</unique> Smite can be cast on enemy champions, dealing reduced true damage and stealing 20% Movement Speed for 2 seconds. <br><unique>UNIQUE Passive - Tooth / Nail:</unique> Basic attacks deal 25 bonus damage vs. monsters. Damaging a monster with a spell or attack steals 30 Health over 5 seconds. Killing monsters grants <font color=\'#99BBBB\'><a href=\'SpecialJungleExperience\'>special bonus experience</a></font>.', 3706),
(457, 'Enchantment: Cinderhulk', 'Grants Health and Immolate Aura', '<stats>+300 Health<br>+20% Bonus Health</stats><br><br><unique>UNIQUE Passive - Immolate:</unique> Deals 11 (+1 per champion level) magic damage a second to nearby enemies while in combat. Deals 200% bonus damage to minions and monsters. ', 1401),
(458, 'Rod of Ages (Quick Charge)', 'Greatly increases Health, Mana, and Ability Power', '<stats>+300 Health<br><mana>+300 Mana</mana><br>+60 Ability Power</stats><br><br><passive>Passive:</passive> Grants +20 Health, +10 Mana, and +4 Ability Power per stack (max +200 Health, +100 Mana, and +40 Ability Power). Grants 1 stack per 40 seconds (max 10 stacks).<br><unique>UNIQUE Passive - Eternity:</unique> 15% of damage taken from champions is gained as Mana. Spending Mana restores 20% of the cost as Health, up to 25 per spell cast.', 3029),
